package com.battelship;

public class Jugador {

	private int tipo;
	private String nombre;

	public Jugador(int tipo, String nombre) {
		// TODO Auto-generated constructor stub
		this.tipo = tipo;
		this.nombre = nombre;
	}

	public int getTipo() {
		return tipo;
	}

	public String getNombre() {
		return nombre;
	}

}
